//const types = ["Température", "Humidité", "Pression", "CO2", "Lumière", "vent"]; pour indication

let index = flow.get("moyenne_index") || 0;
let val = msg.payload[0].value;

const min = [-10, 0, 950, 400, 0, 0];
const max = [40, 100, 1050, 2000, 500, 70];


// Clamp la valeur dans les bornes
const ratio = Math.min(Math.max((val - min[index]) / (max[index] - min[index]), 0), 1);

// Détermine le nombre de LEDs à allumer (1 à 8)
const ledCount = Math.round(ratio * 7) + 1;

const lux2 = Math.round(255*4*(ratio-ratio**(2)));
const lux1 = Math.round(255*ratio-lux2/2) ;
const lux3 = Math.round(255*(1-ratio)-lux2/2); 

const co1 = Math.round(255 * (1 - ratio));
const co2 =Math.round(255 * ratio);

// Couleur dégradée du rouge (min) au vert (max)
const r = [co2, co1, co2, 255, lux1+lux2+lux3/4,co2];
const g = [0, co1, co2, co2, lux1/2+lux2+lux3/4,255];
const b = [co1, 255, 255, 0, lux2+lux3,co2];

// Génère un tableau de 8 LEDs
const leds = [];
for (let i = 0; i < 8; i++) {
    if (i < ledCount) {
        leds.push([r[index], g[index], b[index]]); // LED allumée
    } else {
        leds.push([0, 0, 0]); // LED éteinte
    }
}

// Envoi à ESPHome via MQTT
msg.topic = "esp32/barre_led/set";
msg.payload = {
    effect: "addressable_lambda",
    brightness: 255,
    color_type: "rgb",
    rgb: leds
};

return msg;

const requetes = [
  { type: "Température",  sql: "SELECT ROUND(AVG(temperature), 1) AS value FROM mesures WHERE date >= NOW() - INTERVAL 5 MINUTE" },
  { type: "Humidité",     sql: "SELECT ROUND(AVG(humidite), 1) AS value FROM mesures WHERE date >= NOW() - INTERVAL 5 MINUTE" },
  { type: "Pression",     sql: "SELECT ROUND(AVG(pression), 1) AS value FROM mesures WHERE date >= NOW() - INTERVAL 5 MINUTE" },
  { type: "CO2",          sql: "SELECT ROUND(AVG(co2), 1) AS value FROM mesures WHERE date >= NOW() - INTERVAL 5 MINUTE" },
  { type: "Lumière",      sql: "SELECT ROUND(AVG(lux), 1) AS value FROM mesures WHERE date >= NOW() - INTERVAL 5 MINUTE" },
  { type: "Vent",         sql: "SELECT ROUND(AVG(vent), 1) AS value FROM mesures WHERE date >= NOW() - INTERVAL 5 MINUTE" }
];

// on ajoute type pour le garder dans chaque msg
msg.payload = requetes;
return msg;

// Liste des seuils
const seuils = {
    "Température": 32,
    "Humidité": 60,
    "Pression": 1015,
    "CO2": 1000,
    "Lumière": 40000,
    "Vent": 20
};

// msg.payload doit être un tableau d'objets comme :
// [{ type: "Température", value: 26.5 }, ...]

let alerte = false;

for (let mesure of msg.payload) {
    const type = mesure.type;
    const valeur = mesure.value ?? null;
    const seuil = seuils[type];

    if (valeur !== null && valeur > seuil) {
        alerte = true;
        break;  // Dès qu'une alerte est détectée, inutile de continuer
    }
}

msg.payload = alerte;  // true ou false
return msg;

msg.topic = msg.payload.sql;
msg.type = msg.payload.type;
return msg;

const min = [-10, 0, 950, 50, 0, 0];
const max = [40, 100, 1050, 40000, 500, 70];
const names = ['temp', 'hum', 'pressure', 'co2', 'lux', 'wind'];

const data = msg.payload || {};
msg.payload = {};

names.forEach((name, i) => {
    const raw = data[name]; // Attention ici : on lit dans msg.payload
    const minVal = min[i];
    const maxVal = max[i];
    const percent = ((raw - minVal) / (maxVal - minVal)) * 100;

    msg.payload[name] = Math.max(0, Math.min(100, +percent.toFixed(1))); // pour affichage visuel
    msg.payload[`${name}Val`] = raw; // pour l'affichage réel
});

return msg;
