    const raw = data[name]; // Attention ici : on lit dans msg.payload
    const minVal = min[i];
    const maxVal = max[i];
    const percent = ((raw - minVal) / (maxVal - minVal)) * 100;

    msg.payload[name] = Math.max(0, Math.min(100, +percent.toFixed(1))); // pour affichage visuel
    msg.payload[`${name}Val`] = raw; // pour l'affichage rÃ©el
});

return msg;

